import React, { useState } from "react";
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Select,
  Modal,
  TextArea,
} from "semantic-ui-react";

const QuizModal = ({ subjectOptions }) => {
  const [open, setOpen] = useState(false);

  const [formState, setFormState] = useState({
    answer1: "",
    answer2: "",
    answer3: "",
    answer4: "",
    question: "",
  });

  const [subject, setSubject] = useState("");
  const [correctAnswer, setCorrectAnswer] = useState("");
  const handleChange = (ev) => {
    setFormState((prev) => ({ ...prev, [ev.target.id]: ev.target.value }));
  };
  const options = [
    { key: "1", text: "Answer 1", value: "answer1" },
    { key: "2", text: "Answer 2", value: "answer2" },
    { key: "3", text: "Answer 3", value: "answer3" },
    { key: "4", text: "Answer 4", value: "answer4" },
  ];
  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Create a quiz</Button>}
    >
      <Modal.Header>Quiz</Modal.Header>
      <Modal.Content>
        <Modal.Description>
          <Form>
            <Form.Field control={TextArea} label="Question" id="question" />
            <Form.Field
              control={Select}
              label="Topic Subject"
              options={subjectOptions}
              placeholder="Subject"
              id="topicSubject"
            />
            <Form.Group widths="equal">
              <Form.Field control={Input} label="Answer 1" id="answer1" />
              <Form.Field control={Input} label="Answer 2" id="answer2" />
            </Form.Group>
            <Form.Group widths="equal">
              <Form.Field control={Input} label="Answer 3" id="answer3" />
              <Form.Field control={Input} label="Answer 4" id="answer4" />
            </Form.Group>
            <Form.Field
              control={Select}
              label="Correct Answer"
              options={options}
            />
            <Form.Field control={Button}>Submit</Form.Field>
          </Form>
        </Modal.Description>
      </Modal.Content>
    </Modal>
  );
};

export default QuizModal;
