import React from "react";
import {
  Icon,
  Menu,
  Sidebar as SideBar,
  Container,
  Button,
  Popup,
  Image,
  Header,
} from "semantic-ui-react";
import UserIcon from "../../images/elliot.jpg";
import { useUserContext } from "../../context/userContext";
import { Link } from "react-router-dom";
import { getSuggestedQuery } from "@testing-library/react";

import AgoraLogo from "../../images/logo.png";
export const Sidebar = ({ children, items }) => {
  const { signOut, currentUser } = useUserContext();
  console.log(AgoraLogo);
  return (
    <SideBar.Pushable style={{backgroundColor:'#ededf5'}}>
      <SideBar
//        width="small"
        size="large"
        as={Menu}
        animation="overlay"
        icon="labeled"
        vertical
        visible={true}
        style={{backgroundColor:'#172b4d'}}
      >
        <Header inverted style={{marginTop:'1em'}} size='huge'> Agora </Header>
        <Menu.Item  as={Link} to="/dashboard">
          <Icon color='green' name="home" />
          <Header inverted>Home</Header>
        </Menu.Item>
        <Menu.Item   as={Link} to="/quizzes">
          <Icon color='green' name="question" />
          <Header inverted>Quizzes</Header>
        </Menu.Item>
        <Menu.Item color='green' as={Link} to="/history">
          <Icon color='green' name="history" />
          <Header inverted>History</Header>
        </Menu.Item>
        <Menu.Item color='green' as={Link} to="/profile">
          <Icon color='green' name="user" />
          <Header inverted>UserProfile</Header>
        </Menu.Item>
      </SideBar>
      <SideBar.Pusher style={{ minHeight: "100vh" }}>
        <Menu color="teal" inverted size="massive" borderless fixed="top" style={{backgroundColor:'#3333ff'}}>
        <Header
          as='h1'
          inverted
          style={{
            fontWeight: 'normal',
            marginBottom: 0,
            marginTop: '1em',
            marginLeft: '12em',
          }}
          />
          <div
          
            style={{
              marginLeft: "auto",
              marginTop:'0.5em',
              display: "flex",
              alignItems: "left",
            }}
          >
            
            <Popup
              header={currentUser.displayname}
              content={currentUser.role+ " "+ currentUser.email}
              trigger={<Image src={currentUser.avatar=== undefined? UserIcon : currentUser.avatar} avatar />}
            />
            <Button negative onClick={() => signOut()}>
              Log out
            </Button>
          </div>
        </Menu>
        <Container fluid style={{ paddingLeft: 300, paddingTop: 80 ,paddingRight: 50}} >
          {children}
        </Container>
      </SideBar.Pusher>
    </SideBar.Pushable>
  );
};
