import { useUserContext } from "../../context/userContext";
import { db } from "../../firebase-config";
import { useEffect, useState } from "react";
import {
  collection,
  query,
  onSnapshot,
  orderBy,
  getDocs,
  where,
} from "firebase/firestore";
import { Sidebar } from "../Dashboard/Sidebar";
import { Link, useParams } from "react-router-dom";
import { Button, Card, Image, Form, Radio } from "semantic-ui-react";
import QuizModal from "../Dashboard/QuizModal";

export const Quiz = () => {
  const { subject } = useParams();
  const { currentUser } = useUserContext();
  const [subjectQuizzes, setSubjectQuizzes] = useState([]);
  useEffect(() => {
    async function getSubjects() {
      const q = query(
        collection(db, "quizzes"),
        where("subject", "==", subject)
      );

      const querySnapshot = await getDocs(q);
      const quizzes = querySnapshot.docs.map((doc) => doc.data());

      setSubjectQuizzes(quizzes);
    }
    getSubjects();
  }, []);

  const handleChange = (e, { value }) => this.setState({ value });

  const options = [{ key: "1", text: subject, value: subject }];

  console.log(subjectQuizzes);
  return (
    <Sidebar>
      <h1>Quiz - {subject}</h1>

      <QuizModal subjectOptions={options} currentUser={currentUser} />

      {subjectQuizzes.map((quiz) => {
        return (
          <Card style={{ width: "100%" }}>
            <Card.Content>
              <Card.Header>{quiz.question}</Card.Header>
              <Card.Description></Card.Description>
            </Card.Content>
            <Card.Content extra>
              <Form.Field>
                <Radio
                  label={quiz.answer1}
                  name="radioGroup"
                  value={quiz.answer1}
                />
              </Form.Field>
              <Form.Field>
                <Radio
                  label={quiz.answer2}
                  name="radioGroup"
                  value={quiz.answer2}
                />
              </Form.Field>
              <Form.Field>
                <Radio
                  label={quiz.answer3}
                  name="radioGroup"
                  value={quiz.answer3}
                />
              </Form.Field>
              <Form.Field>
                <Radio
                  label={quiz.answer4}
                  name="radioGroup"
                  value={quiz.answer4}
                />
              </Form.Field>
              {/* 
                {quiz.}
              <Form.Field>
                <Radio
                  label="Choose this"
                  name="radioGroup"
                  value="this"
                  checked={this.state.value === "this"}
                  onChange={this.handleChange}
                />
              </Form.Field>
              <Form.Field>
                <Radio
                  label="Or that"
                  name="radioGroup"
                  value="that"
                  checked={this.state.value === "that"}
                  onChange={this.handleChange}
                />
              </Form.Field> */}
            </Card.Content>
          </Card>
        );
      })}
    </Sidebar>
  );
};
