import React, { useState } from "react";
import { Button, Header, Modal, Form, Input } from "semantic-ui-react";
import { doc, setDoc, Timestamp } from "firebase/firestore";
import { useUserContext } from "../../context/userContext";
import { firestore } from "../../firebase-config";
import { async } from "@firebase/util";

export function SubjectModal() {
  const [open, setOpen] = React.useState(false);
  const [subject, setSubject] = useState("");
  const { currentUser } = useUserContext();
  console.log(currentUser);
  const handleSubject = async (ev) => {
    ev.preventDefault();
    try {
      const subjectRef = doc(firestore, "subject");
      await setDoc(subjectRef, { title: subject, user: currentUser.uid });
      setOpen(false);
    } catch {}
  };
  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Create a Subject</Button>}
    >
      <Modal.Header>Subject</Modal.Header>
      <Modal.Content>
        <Modal.Description>
          <Header>Add a subject</Header>
          <Form>
            <Form.Group>
              <Form.Field
                control={Input}
                label="Subject Title"
                placeholder="Title...."
                onChange={(ev) => setSubject(ev.target.value)}
                value={subject}
              />
            </Form.Group>
            <Form.Field control={Button}>Submit</Form.Field>
          </Form>
        </Modal.Description>
      </Modal.Content>
    </Modal>
  );
}
