import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { Sidebar } from "../Dashboard/Sidebar";
import { Button, Checkbox, Form } from "semantic-ui-react";
import { v4 as uuidv4 } from "uuidv4";

export const Topic = () => {
  const { topicId } = useParams();

  const [formState, setFormState] = useState("");

  // useEffect(() => {
  //   async function getTopics() {
  //     try {
  //       const q = query(collection(db, "topics"), orderBy("createdAt", "desc"));

  //       const unsub = await onSnapshot(q, (querySnapshot) => {
  //         const topics = querySnapshot.docs.map((topic) => topic.data());
  //         setTopics(topics);
  //       });

  //       return unsub;
  //     } catch (err) {
  //       console.log(err);
  //     }
  //   }
  //   return getTopics();
  // }, []);
  const handlePost = async (ev) => {
    ev.preventDefault();
    try {
      ev.preventDefault();

      if (formState === "") {
        return;
      }

      const topicCollRef = collection(db, "posts");
      // const subjectCollRef = collection(db, "subject");
      await addDoc(topicCollRef, {
        ...formState,
        user: currentUser.email,
        uid: currentUser.uid,
        topicId: uuidv4(),
        createdAt: serverTimestamp(),
      });
    } catch (err) {
      console.log(err);
    }
  };
  return (
    <Sidebar>
      Particular topic
      <Form>
        <Form.Field>
          <input
            value={formState}
            placeholder="Add a post"
            onChange={(ev) => {
              setFormState(ev.target.value);
            }}
          />
        </Form.Field>

        <Button type="submit">Submit</Button>
      </Form>
    </Sidebar>
  );
};
