import React, { useState } from "react";
import { Icon, Menu, Sidebar, Container } from "semantic-ui-react";
import { useMediaQuery } from "react-responsive";
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";
import Home from "../pages/Home";
import About from "../pages/About";
import Users from "../pages/Users";

export default function MainLayout({ children, items }: MainLayoutProps) {
  // s

  return (
    <Sidebar.Pushable>
      <Sidebar
        width="thin"
        size="large"
        as={Menu}
        animation="overlay"
        icon="labeled"
        vertical
        visible={true}
      >
        {items.map((item, index) => (
          <Menu.Item
            active={activeItem === index}
            as={Link}
            key={index}
            onClick={handleItemClick(index)}
            {...item}
          />
        ))}
      </Sidebar>
      <Sidebar.Pusher
        dimmed={isDesktopOrLaptop ? false : visible}
        onClick={handlePusher}
        style={{ minHeight: "100vh" }}
      >
        <Menu color="teal" inverted size="massive" borderless fixed="top">
          <Menu.Item onClick={handleToggle}>
            <Icon name="sidebar" />
          </Menu.Item>
          <Menu.Item
            style={topMenuStyle}
            header
            content={items[activeItem].content}
          />
        </Menu>
        <Container fluid style={containerStyle}>
          {children}
          <Switch>
            <Route path="/" exact component={Home} />
            <Route path="/about" component={About} />
            <Route path="/users" component={Users} />
          </Switch>
        </Container>
      </Sidebar.Pusher>
    </Sidebar.Pushable>
  );
}
