import React from "react";
import {
  Icon,
  Menu,
  Sidebar as SideBar,
  Container,
  Button,
  Popup,
  Image,
} from "semantic-ui-react";
import UserIcon from "../../images/elliot.jpg";
import { useUserContext } from "../../context/userContext";

export const Sidebar = ({ children, items }) => {
  const { signOut, currentUser } = useUserContext();
  return (
    <SideBar.Pushable>
      <SideBar
        width="thin"
        size="large"
        as={Menu}
        animation="overlay"
        icon="labeled"
        vertical
        visible={true}
      >
        <Menu.Item as="a">
          <Icon name="home" />
          Home
        </Menu.Item>
        <Menu.Item as="a">
          <Icon name="gamepad" />
          Games
        </Menu.Item>
        <Menu.Item as="a">
          <Icon name="camera" />
          Channels
        </Menu.Item>
      </SideBar>
      <SideBar.Pusher style={{ minHeight: "100vh" }}>
        <Menu color="teal" inverted size="massive" borderless fixed="top">
          <div
            style={{
              marginLeft: "auto",
              display: "flex",
              alignItems: "center",
            }}
          >
            <Popup
              header={currentUser.email}
              content={currentUser.role}
              trigger={<Image src={UserIcon} avatar />}
            />
            <Button negative onClick={() => signOut()}>
              Log out
            </Button>
          </div>
        </Menu>
        <Container fluid style={{ paddingLeft: 165, paddingTop: 65 }}>
          {children}
        </Container>
      </SideBar.Pusher>
    </SideBar.Pushable>
  );
};
