import { doc, getDoc } from "firebase/firestore";
import React, { useState, useEffect } from "react";
import {
  Button,
  Header,
  Modal,
  Select,
  Form,
  Input,
  TextArea,
} from "semantic-ui-react";
import { collection, query, where, getDocs } from "firebase/firestore";
import { db } from "../../firebase-config";

const initialState = {
  topicTitle: "",
  topicSubject: "",
  topicDescription: "",
};
export function QuestionModal() {
  const [open, setOpen] = React.useState(false);

  const [subjectOptions, setSubjectOptions] = useState([]);
  const [formState, setFormState] = useState(initialState);
  const handleChange = (ev) => {
    setFormState((prev) => ({ ...prev, [ev.target.id]: ev.target.value }));
  };

  useEffect(() => {
    async function getSubjects() {
      const q = query(collection(db, "subject"));

      const querySnapshot = await getDocs(q);
      const subjects = querySnapshot.docs.map((doc, id) => ({
        value: doc.data().title,
        key: id,
        text: doc.data().title,
      }));
      console.log(subjects);
      setSubjectOptions(subjects);
    }
    getSubjects();
  }, []);

  return (
    <Modal
      onClose={() => setOpen(false)}
      onOpen={() => setOpen(true)}
      open={open}
      trigger={<Button>Create</Button>}
    >
      <Modal.Header>Topic</Modal.Header>
      <Modal.Content>
        <Modal.Description>
          <Header>Add a topic</Header>
          <Form>
            <Form.Group>
              <Form.Field
                control={Input}
                label="topic"
                placeholder="Add a topic"
                value={formState.topicTitle}
                onChange={handleChange}
              />
            </Form.Group>

            <Form.Field
              control={Select}
              label="Subject"
              options={subjectOptions}
              placeholder="Subject"
              onChange={handleChange}
              value={formState.topicSubject}
            />
            <Form.Field
              control={TextArea}
              label="Topic Description"
              placeholder="Add a topic description"
              onChange={handleChange}
              value={formState.topicTitle}
            />

            <Form.Field control={Button}>Submit</Form.Field>
          </Form>
          )
        </Modal.Description>
      </Modal.Content>
      <Modal.Actions>
        <Button color="black" onClick={() => setOpen(false)}>
          Nope
        </Button>
        <Button
          content="Yep, that's me"
          labelPosition="right"
          icon="checkmark"
          onClick={() => setOpen(false)}
          positive
        />
      </Modal.Actions>
    </Modal>
  );
}
